// 获取文档滚动高度
function getScrollTop() {
  var scrollTop = 0;
  if (document.documentElement && document.documentElement.scrollTop) {
    scrollTop = document.documentElement.scrollTop;
  }else if (document.body) {
    scrollTop = document.body.scrollTop;
  }
  return scrollTop;
}

//获取当前可视范围的高度
function getClientHeight() {
  var clientHeight = 0;
  if (document.body.clientHeight && document.documentElement.clientHeight) {
    clientHeight = Math.min(document.body.clientHeight, document.documentElement.clientHeight);
  }else {
    clientHeight = Math.max(document.body.clientHeight, document.documentElement.clientHeight);
  }
  return clientHeight;
}

//获取文档完整的高度
function getScrollHeight() {
  return Math.max(document.body.scrollHeight, document.documentElement.scrollHeight);
}

// 获取元素到页面顶部的距离
function getElementTop(elem){
  var elemTop=elem.offsetTop;//获得elem元素距相对定位的父元素的top
  elem=elem.offsetParent;//将elem换成起相对定位的父元素
  while(elem!=null){//只要还有相对定位的父元素
    //获得父元素 距他父元素的top值,累加到结果中
    elemTop+=elem.offsetTop;
    //再次将elem换成他相对定位的父元素上;
    elem=elem.offsetParent;
  }
  return elemTop;
}

// 格式化时间
// 对Date的扩展，将 Date 转化为指定格式的String
// 月(M)、日(d)、小时(h)、分(m)、秒(s)、季度(q) 可以用 1-2 个占位符，
// 年(y)可以用 1-4 个占位符，毫秒(S)只能用 1 个占位符(是 1-3 位的数字)
Date.prototype.Format = function (fmt) { //author: meizz
  var o = {
      "M+": this.getMonth() + 1, //月份
      "w+": (function(that){
        var fullYear = that.getFullYear(),dayTime = 86400000;
        var d1 = new Date(fullYear+'-01-01').getTime();
        var d1W = new Date(fullYear+'-01-01').getDay();
        var d2 = that.getTime();
        var offsetTime = d2-d1-(d1W?(7-d1W):d1W)*dayTime; 
        var weekNum = Math.ceil(offsetTime/dayTime/7);
        return (d1W>0?1:0)+weekNum;
      })(this),
      "d+": this.getDate(), //日
      "h+": this.getHours(), //小时
      "m+": this.getMinutes(), //分
      "s+": this.getSeconds(), //秒
      "q+": Math.floor((this.getMonth() + 3) / 3), //季度
      "S": this.getMilliseconds() //毫秒
  };
  if (/(y+)/.test(fmt)){
    fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
  }
  for (var k in o){
    if (new RegExp("(" + k + ")").test(fmt)){
      fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
    }
  }
  return fmt;
}

// base64图片进二进行文件（Blob）
function dataURItoBlob(base64Data) {
  var byteString;
  if (base64Data.split(',')[0].indexOf('base64') >= 0) {
    byteString = atob(base64Data.split(',')[1]);
  } else {
    byteString = unescape(base64Data.split(',')[1]);
  }
  var mimeString = base64Data.split(',')[0].split(':')[1].split(';')[0];
  var ia = new Uint8Array(byteString.length);
  for (var i = 0; i < byteString.length; i++) {
    ia[i] = byteString.charCodeAt(i);
  }
  return new Blob([ia], {type:mimeString});
}

// 对象数组查询
function searchArr(arr,ind,val){
  arr=Array.isArray(arr)?arr:[];
  for(var i=0;i<arr.length;i++){
    var one=arr[i];
    if(one[ind]==val){
      return {result:true,data:one,index:i};
    }
  }
  return {result:false,data:null,index:-1};
}

// 自定义提示弹窗
function alertModelMsg(msg,fun,num){
  num=parseInt(isNaN(num)?0:num);

  var d=document.createElement("div");
  d.style.cssText="position:fixed;top:0;left:0;right:0;bottom:0;z-index:9000;";
  d.innerHTML="<p style='box-sizing:border-box;position:absolute;bottom:15%;left:50%;-webkit-transform:translate(-50%,0%);-moz-transform:translate(-50%,0%);-ms-transform:translate(-50%,0%);transform:translate(-50%,0%);max-width:80%;line-height:25px;font-size:16px;color: #fff;text-align: center;padding: 5px 10px;background-color: rgba(0,0,0,0.6);border-radius:5px;'>"+msg+"</p>";

  document.body.append(d);

  setTimeout(function(){

    d.parentNode.removeChild(d);
    if(typeof(fun)=="function"){
      fun();
    }

  },num>0?num:2000);

}

