// loading 加载
function loadingFun () {
  var d = document.createElement('div');
  d.id = 'loadingFunAlert';
  d.style.cssText = 'position:fixed;top:0;left:0;right:0;bottom:0;background-color:transparent;z-index:1000;';
  d.innerHTML = "<img style='position: absolute;top: 50%;left: 50%;width: 12%;transform: translate(-50%,-50%);max-width: 50px;' src='data:image/gif;base64,'/>";

  var idObject = document.getElementById('loadingFunAlert');
  if (idObject == null) {
    document.body.append(d);
    document.getElementById('loadingFunAlert').onclick = function () {
      var target = this;
      target.parentNode.removeChild(target);
    }
  }

}

// loading 关闭
function loadingClose () {
  // $("#loadingFunAlert").remove();
  var idObject = document.getElementById('loadingFunAlert');
  if (idObject != null) {
    idObject.parentNode.removeChild(idObject);
  }
}

// 格式化数据  传入数据，保留位数
function numFormat (num, n) {
  var offset = Math.pow(10, n+1);
  // console.log(offset);
  return (Math.round (num * offset) / offset).toFixed(n);
}

// 计算字符串宽度  传入字符串 及字体大小(默认PX);
function calcStrWidth(str,size){
  var _c = document.createElement('canvas');
  var _ctx=_c.getContext("2d");
  _ctx.font= ''+parseInt(size)+"px Arial";
  // console.log(_ctx.measureText(''+str).width);
  return Math.ceil(_ctx.measureText(str).width);
}

// 深拷贝
function deepCopy(obj){
  var resData = null;
  if(typeofString(obj)=='Object' || typeofString(obj)=='Array'){
    var isLoop = false;
    try{
      JSON.stringify(obj);
    }catch(err){
      isLoop = true;
      resData = obj;
    }

    if(!isLoop){
      resData = typeofString(obj)=='Object'?{}:[];
      for(var k in obj){
        if(typeofString(obj[k])=='Object' || typeofString(obj[k])=='Array'){
          resData[k] = deepCopy(obj[k]);
        }else{
          resData[k] = obj[k];
        }
      }
    }

  }else{
    resData = obj;
  }

  return resData;
}

// 树型对象数据查找  data 查询数据  recursiveKey 递归key  searchKey 查询key  searchVal 查询值
function recursiveSearch({ data, recursiveKey, searchKey, searchVal }){
  var res = {
    result: false,
    data: null
  }
  if(Array.isArray(data)){
    for(var i = 0;i<data.length;i++){
      var one = data[i];
      if(one[searchKey] == searchVal){
        res.result = true;
        res.data = one;
        break;
      }
      if(Array.isArray(one[recursiveKey])){
        var deepRes = recursiveSearch({
          data: one[recursiveKey],
          recursiveKey: recursiveKey,
          searchKey: searchKey,
          searchVal: searchVal
        });
        if(deepRes.result){
          res.result = deepRes.result;
          res.data = deepRes.data;
          break;
        }
      }
    }
  }
  return res;
}
// 树型对象数据批量查找   data 查询数据  recursiveKey 递归key  searchKey 查询key  searchVal 查询值   返回data为数组
function recursiveSearchArr({ data, recursiveKey, searchKey, searchVal }){
  var res = {
    result: false,
    data: []
  }
  if(Array.isArray(data)){
    for(var i = 0;i<data.length;i++){
      var one = data[i];
      if(one[searchKey] == searchVal){
        res.result = true;
        res.data.push(one);
      }
      if(Array.isArray(one[recursiveKey])){
        var deepRes = recursiveSearchArr({
          data: one[recursiveKey],
          recursiveKey: recursiveKey,
          searchKey: searchKey,
          searchVal: searchVal
        });
        if(deepRes.result){
          res.result = deepRes.result;
          res.data = res.data.concat(deepRes.data);
        }
      }
    }
  }
  return res;
}

// 类型检测
function typeofString(obj){
  var res = Object.prototype.toString.call(obj);
  var typeData = res.replace(/[\[\]]/g,'').split(' ');
  return typeData[1];
}

//获取指定名称的cookie的值
function getCookie(objName) {//获取指定名称的cookie的值
  var arrStr = document.cookie.split("; ");
  for (var i = 0; i < arrStr.length; i++) {
      var temp = arrStr[i].split("=");
      if (temp[0] == objName) return unescape(temp[1]);  //解码
  }
  return "";
}

